module.exports = require('./lib/client');
var fs = require('fs');
var config = require('./config.js');
var db = require('orchestrate')(config.dbKey);
var dir = '../../Pride-And-Prejudice-master';

fs.readdir(dir, function(err, readChapters) {
  for (var i= 0; i <= 61; i++) {
    var buildName = '../../Pride-And-Prejudice-master/' + readChapters[i];
    fs.readFile(buildName, {"encoding": "utf-8"}, function(err, fileContents) {
      if (err) throw err;
      console.log(fileContents);
      db.put('Pride_N_Prejudice', 'chapter' + i, {
       "text": "chapter",
      });
    });
  };
      console.log(readChapters); 
});
